INSERT INTO `sys_oper_log` VALUES ('289', '课程', '2', 'com.instrument.music.controller.MusicCurriculumController.edit()', 'PUT', '1', 'admin', null, '/music/curriculum', '127.0.0.1', '内网IP', '{\"blogPicLink\":\"\",\"blogPicType\":\"0\",\"instrumentId\":23,\"updateTime\":1714210521876,\"params\":{},\"title\":\"测试乐器学习\",\"type\":\"0\",\"content\":\"川音作曲系硕士，这么些年见到过许多朋友，比如业余时间不充裕想学一点乐器充实自己，乐理知识不懂但想自己写歌，乐谱不认识想弹奏钢琴等等\\n看过网上大多数教程，一来就讲五线谱、五度圈等等的比比皆是，实际上这类视频还是给有相当一部分基础的朋友学习的，那么我们推出的这个教程就是针对真正完全零基础，do re mi都不知道的朋友，我们从最最基础的地方开始讲解，帮助你在音乐这条路上一步一步前行\",\"blogPic\":\"\",\"createBy\":\"admin\",\"top\":\"0\",\"createTime\":1714147722000,\"updateBy\":\"admin\",\"id\":24,\"status\":\"0\"}', '{\"msg\":\"操作成功\",\"code\":200}', '0', null, '2024-04-27 17:35:21');
INSERT INTO `sys_oper_log` VALUES ('290', '乐器', '2', 'com.instrument.music.controller.MusicInstrumentController.edit()', 'PUT', '1', 'admin', null, '/music/instrument', '127.0.0.1', '内网IP', '{\"createBy\":\"admin\",\"createTime\":1713610146000,\"updateBy\":\"admin\",\"name\":\"吉他\",\"updateTime\":1714214347844,\"id\":23,\"params\":{},\"content\":\"吉他（意大利语：Chitarra），又译为结他或六弦琴。是一种弹拨乐器，通常有六条弦，形状与提琴相似。吉他在流行音乐、摇滚音乐、蓝调、民歌、佛朗明哥中，常被视为主要乐器。而在古典音乐的领域里，吉他常以独奏或二重奏的型式演出；当然，在室内乐和管弦乐中，吉他亦扮演着相当程度的陪衬角色。古典吉他与小提琴、钢琴并列为世界著名三大乐器。\\n\\n吉他主要有民谣吉他、古典吉他和电吉他三种，民谣吉他最常见也最容易上手。三种类型的吉他各有千秋，喜欢听弹唱的一般是民谣吉他；喜欢独奏的一般是古典吉他；喜欢摇滚的，电吉他必不可少。\"}', '{\"msg\":\"操作成功\",\"code\":200}', '0', null, '2024-04-27 18:39:07');
